package RC;

public class Switchcase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      String name = "sarika";
      switch(name) {
      case "sarika":
    	  System.out.println("good");  //valid case
    	  break;
    	  
      case "xys":
    	  System.out.println("bad");
    	  break;
    	  
      default:
    	  System.out.println("gsggs"); 
      }
      
      
	}

}
